    svg: `    <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg"><g id="SVGRepo_bgCarrier" stroke-width="0"></g><g id="SVGRepo_tracerCarrier" stroke-linecap="round" stroke-linejoin="round"></g><g id="SVGRepo_iconCarrier"> <path d="M12 15C13.6569 15 15 13.6569 15 12C15 10.3431 13.6569 9 12 9C10.3431 9 9 10.3431 9 12C9 13.6569 10.3431 15 12 15Z" fill="currentColor"></path> <path fill-rule="evenodd" clip-rule="evenodd" d="M12 3C12.5523 3 13 3.44772 13 4V5.07089C16.0657 5.5094 18.4906 7.93431 18.9291 11H20C20.5523 11 21 11.4477 21 12C21 12.5523 20.5523 13 20 13H18.9291C18.4906 16.0657 16.0657 18.4906 13 18.9291V20C13 20.5523 12.5523 21 12 21C11.4477 21 11 20.5523 11 20V18.9291C7.93431 18.4906 5.5094 16.0657 5.07089 13H4C3.44772 13 3 12.5523 3 12C3 11.4477 3.44772 11 4 11H5.07089C5.5094 7.93431 7.93431 5.5094 11 5.07089V4C11 3.44772 11.4477 3 12 3ZM7 12C7 9.23858 9.23858 7 12 7C14.7614 7 17 9.23858 17 12C17 14.7614 14.7614 17 12 17C9.23858 17 7 14.7614 7 12Z" fill="currentColor"></path> </g></svg>`,
    buyed: false,
    canBuy: false,
  },
]);

const buy = () => {
  if (moreInfos) {
    moreInfos.value.buyed = true;
    moreInfos.value.canBuy = false;

    oceanStates.value.push(moreInfos.value.environnement);
    partsStates.value.push(moreInfos.value.organe);

    actions.value = actions.value.map((action) =>
      action.id === moreInfos.value.id ? moreInfos.value : action
    );

    // Get the next and set canBuy to true
    const nextAction = actions.value.find(
      (action) => action.id === moreInfos.value.id + 1
    );
    if (nextAction) {
      nextAction.canBuy = true;
      actions.value = actions.value.map((action) =>
        action.id === nextAction.id ? nextAction : action
      );
    }

    moreInfos.value = null;
  }
};

import { ref } from "vue";

const startgame = ref<boolean>(true);
const start = () => {
  startgame.value = false;
};
</script>

